declare function cookieCutter(doc?: any): any;

declare module 'cookie-cutter' {
    export interface Cookie {
        get(key: string): string | undefined;
        set(key: string, value: string | null, opts?: CookieOpts): string
    }

    export interface CookieOpts {
        expires?: Date | number;
        path?: string;
        domain?: string;
        secure?: boolean;
    }

    const cookie: Cookie;
    export default cookie;
}